styling.custom_css:
.summary-node-container {
    display: flex;
    align-items: center;
    padding: 16px 20px;
    background: #1f1f1f;
    border: 1.5px solid #06b6d4;
    border-radius: 8px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
    transition: all 0.2s ease;
    width: 220px;
    height: 100px;
    position: relative;
}
.summary-node-container:hover {
    border-color: #22d3ee;
    box-shadow: 0 4px 12px rgba(6, 182, 212, 0.2);
}
.summary-icon { margin-right: 12px; flex-shrink: 0; }
.summary-content { flex: 1; display: flex; flex-direction: column; justify-content: center; }
.summary-title { font-size: 13px; font-weight: 500; color: #ffffff; margin-bottom: 2px; line-height: 1.2; }
.summary-subtitle { font-size: 11px; color: #06b6d4; opacity: 0.9; line-height: 1.2; }

.query-node-container {
    display: flex;
    align-items: center;
    padding: 16px 20px;
    background: #1f1f1f;
    border: 1.5px solid #06b6d4;
    border-radius: 9999px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
    transition: all 0.2s ease;
    transform-origin: center center;
    width: 220px;
    height: 90px;
    position: relative;
}
.query-node-container:hover {
    border-color: #22d3ee;
    box-shadow: 0 4px 12px rgba(6, 182, 212, 0.2);
}
.query-icon { margin-right: 12px; flex-shrink: 0; color: #06b6d4; display: flex; align-items: center; }
.query-icon svg { width: 20px; height: 20px; }
.query-content { flex: 1; display: flex; flex-direction: column; justify-content: center; gap: 2px; }
.query-title { font-size: 13px; font-weight: 500; color: #ffffff; margin-bottom: 2px; line-height: 1.2; }
.query-subtitle { font-size: 11px; color: #06b6d4; opacity: 0.9; line-height: 1.2; }
.query-preview {
    margin-top: 2px;
    font-size: 11px;
    color: #cbd5e1;
    opacity: 0.9;
    max-width: 160px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.response-node-container {
    display: flex;
    align-items: center;
    padding: 16px 20px;
    background: #1f1f1f;
    border: 1.5px solid #10b981;
    border-radius: 9999px;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
    transition: all 0.2s ease;
    transform-origin: center center;
    width: 220px;
    height: 90px;
    position: relative;
}
.response-node-container:hover {
    border-color: #34d399;
    box-shadow: 0 4px 12px rgba(16, 185, 129, 0.2);
}
.response-icon { margin-right: 12px; flex-shrink: 0; color: #10b981; display: flex; align-items: center; }
.response-icon svg { width: 20px; height: 20px; }
.response-content { flex: 1; display: flex; flex-direction: column; justify-content: center; gap: 2px; }
.response-title { font-size: 13px; font-weight: 500; color: #ffffff; margin-bottom: 2px; line-height: 1.2; }
.response-subtitle { font-size: 11px; color: #10b981; opacity: 0.9; line-height: 1.2; }
.response-preview {
    margin-top: 2px;
    font-size: 11px;
    color: #cbd5e1;
    opacity: 0.9;
    max-width: 160px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
